let counter = 1;

function tambahForm() {
    counter++;

    const container = document.getElementById('form-container');

    const newForm = document.createElement('div');
    newForm.classList.add('form-group');

    const row = document.createElement('div');
    row.classList.add('row');

    // Input untuk sub_alt
    const colSubAlt = document.createElement('div');
    colSubAlt.classList.add('col');

    const labelSubAlt = document.createElement('label');
    labelSubAlt.classList.add('form-control-label');
    labelSubAlt.setAttribute('for', 'sub_alt_' + counter);
    labelSubAlt.textContent = 'Sub Alternatif ' + counter;

    const inputSubAlt = document.createElement('input');
    inputSubAlt.setAttribute('type', 'text');
    inputSubAlt.setAttribute('id', 'sub_alt_' + counter);
    inputSubAlt.setAttribute('class', 'form-control');
    inputSubAlt.setAttribute('name', 'sub_alt_' + counter);
    inputSubAlt.setAttribute('placeholder', 'Isikan Sub Kriteria');

    
    colSubAlt.appendChild(labelSubAlt);
    colSubAlt.appendChild(inputSubAlt);

    row.appendChild(colSubAlt);
    newForm.appendChild(row);

    container.appendChild(newForm);
}

function hapusForm() {
    if (counter > 1) {
        const container = document.getElementById('form-container');
        container.removeChild(container.lastChild);
        counter--;
    }
}